import { FFmpegOperationType, FFmpegTemplate } from "./types";

import { create } from "zustand";

interface FFmpegPanelState {
  selectedOperations: FFmpegOperationType[];
  paramValues: Record<string, any>;
  templates: FFmpegTemplate[];
  currentTemplateId: string | null;
  setSelectedOperations: (ops: FFmpegOperationType[]) => void;
  setParamValues: (values: Record<string, any>) => void;
  saveTemplate: (
    tpl: Omit<FFmpegTemplate, "id" | "createdAt" | "updatedAt">
  ) => void;
  deleteTemplate: (id: string) => void;
  applyTemplate: (id: string) => void;
  // 预留异步action
  fetchTemplates?: () => Promise<void>;
  saveTemplateToServer?: (tpl: FFmpegTemplate) => Promise<void>;
}

export const useFFmpegPanelStore = create<FFmpegPanelState>((set, get) => ({
  selectedOperations: [],
  paramValues: {},
  templates: [],
  currentTemplateId: null,
  setSelectedOperations: (ops) => set({ selectedOperations: ops }),
  setParamValues: (values) => set({ paramValues: values }),
  saveTemplate: (tpl) => {
    const id = Date.now().toString();
    const now = Date.now();
    set((state) => ({
      templates: [
        ...state.templates,
        { ...tpl, id, createdAt: now, updatedAt: now },
      ],
    }));
  },
  deleteTemplate: (id) =>
    set((state) => ({
      templates: state.templates.filter((t) => t.id !== id),
      currentTemplateId:
        state.currentTemplateId === id ? null : state.currentTemplateId,
    })),
  applyTemplate: (id) => {
    const tpl = get().templates.find((t) => t.id === id);
    if (tpl) {
      set({
        selectedOperations: tpl.operations,
        paramValues: tpl.params,
        currentTemplateId: id,
      });
    }
  },
  // 预留异步action
  fetchTemplates: undefined,
  saveTemplateToServer: undefined,
}));
